    /* Your CSS styles for the slideshow go here */
    /* Slideshow container */
    .slideshow-container {
      width: 100%; /* Set the desired width for the container. I need this to match the image ratio, as it does now for image 2 */
      &lt;!-- height: 100px; /* Set the desired height for the container */ --&gt;
      overflow: hidden; /* Hide any overflowing content */
      position: relative;
      margin: auto;
      border: 2px solid #068194; /* Add a border around the container */
    }

    .image-container img {
    width: 100%; /* Take up the full width of the container */
      height: 100%; /* Take up the full height of the container */
      object-fit: cover; /* Maintain aspect ratio and cover entire container */
      display: block; /* Remove any extra spacing caused by inline display */
  }

    /* Hide the images by default */
    .mySlides {
      display: none;
    }

    /* Next & previous buttons */
    .prev, .next {
      cursor: pointer;
      position: absolute;
      top: 50%;
      width: auto;
      margin-top: -22px;
      padding: 16px;
      color: white;
      font-weight: bold;
      font-size: 18px;    
      transition: 0.6s ease;
      border-radius: 0 3px 3px 0;
      user-select: none;
    }

    /* Caption text */
    .text {
      color: #f2f2f2;
        font-size: 48px;
      padding: 8px 12px;
      position: absolute;
      bottom: 8px;
      width: 100%;
      text-align: center;
    }

    /* Text overlay */
    .text-overlay {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      color: red; /* Text color */
        z-index: 1; /* Place the text above the image */
    }

    /* Number text (1/3 etc) */
    .numbertext {
      color: #f2f2f2;
        font-size: 12px;
      padding: 8px 12px;
      position: absolute;
      top: 0;
    }

    /* Fading animation */
    .fade {
      animation-name: fade;
      animation-duration: 30s;
    }

    @keyframes fade {
      from {opacity: .8}
      to {opacity: 1}
    }
  &lt;/style&gt;

  &lt;script&gt;
    // Your JavaScript code for the slideshow goes here
    let slideIndex = 0;
    showSlides();

    function showSlides() {
      let i;
      let slides = document.getElementsByClassName("mySlides");
      for (i = 0; i &lt; slides.length; i++) {
        slides[i].style.display = "none";
      }
      slideIndex++;
      if (slideIndex &gt; slides.length) {slideIndex = 1}
      slides[slideIndex-1].style.display = "block";
      setTimeout(showSlides, 5000); // Change image every 2 seconds
    }
  &lt;/script&gt;
read_yaml &lt;- function(file) {
  yaml::yaml.load_file(file)
}

readable_list &lt;- function(s) {
  if (length(s) &lt; 3) {
    return(paste(collapse = " and ", s))
  }
  a &lt;- s[-length(s)]
  b &lt;- s[length(s)]
  return(paste(paste(collapse = ", ", a), " and ", b))
}

button &lt;- function(url, str, icon) {
  icon_base &lt;- substr(icon, 1, 2)
  return(paste(
    "&lt;a class='btn btn-outline-dark btn-sm' href='", url,
    "' target='_blank' rel='noopener noreferrer'&gt;",
    "&lt;i class='", icon_base, " ", icon,
    "' role='img' aria-label='", str, "'&gt;&lt;/i&gt;", str,
    "&lt;/a&gt;", sep = ""
  ))
}

